// src/components/VennDiagram.js
import React, { useEffect, useRef } from "react";
import * as d3 from "d3";
import venn from "venn.js";

const VennDiagramComponent = () => {
  const vennRef = useRef(null);

  useEffect(() => {
    // Clear the previous diagram if any
    d3.select(vennRef.current).selectAll("*").remove();

    // Define the sets and their sizes and intersections
    const sets = [
      { sets: ["A"], size: 12 },
      { sets: ["B"], size: 12 },
      { sets: ["A", "B"], size: 2 },
    ];

    // Create a Venn diagram
    const chart = venn.VennDiagram();

    // Render the diagram
    d3.select(vennRef.current).datum(sets).call(chart);

    // Add hover functionality
    d3.selectAll(".venn-circle path")
      .style("fill-opacity", 0.5)
      .on("mouseover", function (event, d) {
        d3.select(this).style("fill-opacity", 0.7);
        d3.select(this)
          .append("title")
          .text(`Set: ${d.sets.join(", ")}\nSize: ${d.size}`);
      })
      .on("mouseout", function (event, d) {
        d3.select(this).style("fill-opacity", 0.5);
        d3.select(this).select("title").remove();
      });
  }, []);

  return <div ref={vennRef} style={{ width: "600px", height: "400px" }}></div>;
};

export default VennDiagramComponent;
