import React, { useEffect, useRef } from "react";
import * as d3 from "d3";
import { VennDiagram } from "d3-venn";

const VennDiagramComponent = () => {
  const vennRef = useRef(null);

  useEffect(() => {
    const sets = [
      { sets: ["A"], size: 12 },
      { sets: ["B"], size: 12 },
      { sets: ["A", "B"], size: 2 },
    ];

    const chart = VennDiagram().width(600).height(400);

    d3.select(vennRef.current).datum(sets).call(chart);

    d3.selectAll(".venn-circle path")
      .style("fill-opacity", 0.5)
      .style("stroke-width", 2)
      .style("stroke", "black");

    d3.selectAll(".venn-circle text")
      .style("font-size", "16px")
      .style("fill", "black");
  }, []);

  return <div ref={vennRef}></div>;
};

export default VennDiagramComponent;
