import React, { useEffect, useState } from "react";
import * as venn from "venn.js";
import * as d3 from "d3";

const Venn1 = () => {
  const [vennData, setVennData] = useState({ setA: 0, setB: 0, setAB: 0 });

  useEffect(() => {
    fetch("/data.txt")
      .then((response) => response.text())
      .then((text) => parseData(text))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const parseData = (text) => {
    const lines = text.trim().split("\n").slice(1);
    let setA = 0,
      setB = 0,
      setAB = 0;

    lines.forEach((line) => {
      const [commonAB, uniqueA, uniqueB] = line.split(/\s+/);
      if (commonAB !== "NA") setAB += 1;
      if (uniqueA !== "NA") setA += 1;
      if (uniqueB !== "NA") setB += 1;
    });

    setVennData({ setA, setB, setAB });
  };

  useEffect(() => {
    if (vennData.setA > 0 || vennData.setB > 0 || vennData.setAB > 0) {
      const sets = [
        { sets: ["A"], size: vennData.setA },
        { sets: ["B"], size: vennData.setB },
        { sets: ["A", "B"], size: vennData.setAB },
      ];

      const buildVenn = venn.VennDiagram().width(550).height(550);

      const vennChart = d3.select("#venn").datum(sets).call(buildVenn);

      vennChart
        .selectAll("path")
        .style("fill-opacity", "1")
        .style("mix-blend-mode", "none");

      d3.select("#venn").selectAll("text").remove();

      let tooltip = d3
        .select("body")
        .append("div")
        .attr("class", "venntooltip");

      d3.selectAll(".venn-area")
        .on("mouseover", function (d, i) {
          venn.sortAreas(vennChart, i);

          let node = d3.select(this).transition();
          node
            .select("path")
            .style("fill-opacity", 0.7)
            .style("stroke", "red")
            .style("stroke-width", "2");
        })
        .on("mousemove", function (event, d) {
          tooltip.transition().duration(400).style("opacity", "0.9");
          tooltip.text(d.size + " users");
          tooltip
            .style("position", "absolute")
            .style("left", event.pageX + "px")
            .style("top", event.pageY - 28 + "px");
        })
        .on("mouseout", function (d, i) {
          let node = d3.select(this).transition();
          tooltip.transition().duration(400).style("opacity", "0");
          d3.select(this).transition("tooltip").duration(400);
          node
            .select("path")
            .style("fill-opacity", 1)
            .style("stroke-width", "0");
          node
            .select("text")
            .style("font-weight", "100")
            .style("font-size", "24px");
        });
    }
  }, [vennData]);

  return (
    <>
      <h4>Area Proportional Venn Diagram in React using venn.js and D3.js</h4>
      <div className="App" id="venn"></div>
    </>
  );
};

export default Venn1;
