import React, { useEffect, useState } from "react";
import venn from "venn.js";
import * as d3 from "d3";
import "./VennDiagramComponent.css"; // Create and style this CSS file as needed

const VennDiagramComponent = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    // Fetch the data from the .txt file
    fetch("./venn_out_data (1).txt")
      .then((response) => response.text())
      .then((text) => {
        const parsedData = JSON.parse(text);
        setData(parsedData);
      })
      .catch((error) =>
        console.error("Error fetching the Venn diagram data:", error)
      );
  }, []);

  useEffect(() => {
    if (data) {
      const chart = venn.VennDiagram();
      d3.select("#venn").datum(data).call(chart);
    }
  }, [data]);

  return <div id="venn">{data ? null : <p>Loading data...</p>}</div>;
};

export default VennDiagramComponent;
